descriptor.controllerScript:
self.onInit = function() {

    var scope = self.ctx.$scope;
    scope.ctx = self.ctx;
    $scope = self.ctx.$scope;
    $scope.buttonColor = self.ctx.settings.defaultColor;
    $scope.buttonLabel = self.ctx.settings.buttonLabel;
    $scope.buttonLogo = self.ctx.settings.buttonLogo;
    $scope.buttonSubtitle = self.ctx.settings.buttonSubtitle;

        $scope.onButtonClick = function($event) {
            if ($event) {
                $event.stopPropagation();
            }
            var descriptors = self.ctx.actionsApi
                .getActionDescriptors(
                    'rowClick');
            self.ctx.actionsApi.handleWidgetAction(
                $event, descriptors[0]);
        };
};

self.onDataUpdated = function() {
